import { useState } from 'react';
import { sendMessage, isTyping } from 'react-chat-engine';
import { sendOutlined, PictureOutlined, SendOutlined } from '@ant-design/icons';
import LoginForm from './LoginForm';

const MessageForm = (props) => {
    const [value, setValue] = useState(''); // Initial Value Of Message Is An Empty String
    //Props To Be Reconstructed
    const { chatId, creds} = props;

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const authObject = {'Project-ID': '67ec309f-4db7-4a2d-8c5f-5310843be5c7', 'User-Name': localStorage.getItem(username), 'User-Secret': localStorage.getItem(password)}

    //Submission Handler
    const handleSubmit = (event) => {
        event.preventDefault(); //Prevents The Browser From Refreshing When Form Is Submitted

        const text = value.trim();

        if (text.length > 0) {
            sendMessage(creds, chatId, { text });
            setValue('');
        }
    }

    //Input Field Handler
    const handleChange = (event) => {
        setValue(event.target.value); //Value Of Input

        isTyping(authObject, chatId, localStorage.getItem('username'));
    }

    //File Upload Handler
    const handleUpload = (event) => {
        sendMessage(creds, chatId, { files: event.target.files, text: '' })
    }
    return (
        <form className="message-form" onSubmit={handleSubmit}>
            <input 
                className="message-input" 
                placeholder="Type a message" 
                value={value} 
                onChange={handleChange} 
                onSubmit={handleSubmit}
                type="text"
            />
            <label htmlFor='upload-button'> 
                <span className='image-button'>
                    <PictureOutlined className='picture-icon' />
                </span>
            </label>
            <input 
                type="file"
                multiple={false}
                id='upload-button'
                style={{ display: 'none' }}
                onChange={handleUpload.bind(this)}    
            />
            <button type="submit" className="send-button">
                <SendOutlined className="send-icon" />
            </button>
        </form>
    )
}

export default MessageForm;
