import React, { Component } from 'react'

import { WebSocket } from 'nextjs-websocket'
export default class Socket extends Component {
    state = {
        client: undefined,
        props: null
    }

    handleEvent(event) {
        const { props } = this
        const eventJSON = JSON.parse(event)

        if (eventJSON.action === 'login_error') {
            console.log(
                `Your login credentials were not correct: \n
                Project ID: ${props.projectID} \n
                Username: ${props.userName} \n
                User Secret: ${props.userSecret}\n
                Double check these credentials to make sure they're correct.\n
                If all three are correct, try resetting the username and secret in the Online Dashboard or Private API.`
            )

        } else if (eventJSON.action === 'is_typing') {
            props.onTyping && props.onTyping(eventJSON.data.id, eventJSON.data.person)

        } else if (eventJSON.action === 'new_chat') {
            props.onNewChat && props.onNewChat(eventJSON.data)

        } else if (eventJSON.action === 'edit_chat') {
            props.onEditChat && props.onEditChat(eventJSON.data)

        } else if (eventJSON.action === 'delete_chat') {
            props.onDeleteChat && props.onDeleteChat(eventJSON.data)

        } else if (eventJSON.action === 'add_person') {
            props.onAddPerson && props.onAddPerson(eventJSON.data)

        } else if (eventJSON.action === 'remove_person') {
            props.onRemovePerson && props.onRemovePerson(eventJSON.data)

        } else if (eventJSON.action === 'new_message') {
            props.onNewMessage && props.onNewMessage(eventJSON.data.id, eventJSON.data.message)

        } else if (eventJSON.action === 'edit_message') {
            props.onEditMessage && props.onEditMessage(eventJSON.data.id, eventJSON.data.message)

        } else if (eventJSON.action === 'delete_message') {
            props.onDeleteMessage && props.onDeleteMessage(eventJSON.data.id, eventJSON.data.message)

        }
    }

    onClose() { this.props.onFailAuth && this.props.onFailAuth(this.props) }

    render() {
        const { 
            publicKey, projectID, 
            userName, 
            userPassword, userSecret, 
            development 
        } = this.props 

        const wsStart = development ? 'ws://' : 'wss://'
        const rootHost = development ? '127.0.0.1:8000' : 'api.chatengine.io'

        const project = publicKey ? publicKey : projectID
        const secret = userPassword ? userPassword : userSecret

        return <WebSocket 
            url={`${wsStart}${rootHost}/person/?publicKey=${project}&username=${userName}&secret=${secret}`}
            onOpen={() => this.props.onConnect && this.props.onConnect(this.props)}
            onClose={this.onClose.bind(this)}
            onMessage={this.handleEvent.bind(this)}
        />
    }
}

export function getApiUrl(props) { 
    if (props && props.development) {
        return 'http://127.0.0.1:8000'
    }
    return 'https://api.chatengine.io'
}

import axios from 'axios'
import * as str from '../../actions'

export function getChats(props, callback) {
    axios.get(
        `${str.getApiUrl(props)}/chats/`,
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
            "User-Name": props.userName,
            "User-Secret": props.userPassword ? props.userPassword : props.userSecret,
        }}
    )

    .then((response) => {
        // Run hook in Axios on GET requests
        props.onGetChats && props.onGetChats(response.data)

        callback && callback(response.data)
    })

    .catch((error) => {
        console.log('Fetch Chats Error', error)
    });
}

import axios from 'axios'
import * as str from '../../actions'

export function newChat(props, data, callback) {
    axios.post(
        `${str.getApiUrl(props)}/chats/`,
        data, 
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
            "User-Name": props.userName,
            "User-Secret": props.userPassword ? props.userPassword : props.userSecret,
        }}
    )

    .then((response) => {
        callback && callback(response.data)
    })

    .catch((error) => {
        console.log('New Chat Error', error)
    });
}

import axios from 'axios'
import * as str from '../../actions'

export function editChat(props, chatId, data, callback) {
    axios.patch(
        `${str.getApiUrl(props)}/chats/${chatId}/`,
        data, 
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
            "User-Name": props.userName,
            "User-Secret": props.userPassword ? props.userPassword : props.userSecret,
        }}
    )

    .then((response) => {
        callback && callback(response.data)
    })

    .catch((error) => {
        console.log('Edit Chat Error', error)
    });
}

import axios from 'axios'
import * as str from '../../actions'

export function deleteChat(props, chatId, callback) {
    axios.delete(
        `${str.getApiUrl(props)}/chats/${chatId}/`,
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
            "User-Name": props.userName,
            "User-Secret": props.userPassword ? props.userPassword : props.userSecret,
        }}
    )

    .then((response) => {
        callback && callback(response.data)
    })

    .catch((error) => {
        console.log('Delete Chat Error', error)
    });
}

import axios from 'axios'
import * as str from '../../actions'

export function getMessages(props, chatId, callback) {
    axios.get(
        `${str.getApiUrl(props)}/chats/${chatId}/messages/`,
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
            "User-Name": props.userName,
            "User-Secret": props.userPassword ? props.userPassword : props.userSecret,
        }}
    )

    .then((response) => {
        // Run hook in Axios on GET requests
        props.onGetMessages && props.onGetMessages(chatId, response.data)

        callback && callback(response.data)
    })

    .catch((error) => {
        console.log('Fetch Messages Error', error)
    });
}

import axios from 'axios'
import * as str from '../../actions'

export function sendMessage(props, chatId, data, callback) {
    let formdata = new FormData()
    if(data.files) {
        for (let i = 0; i < data.files.length; i++) { 
            formdata.append('attachments', data.files[i], data.files[i].name)
        }
    }
    formdata.append('text', data.text)

    axios.post(
        `${str.getApiUrl(props)}/chats/${chatId}/messages/`,
        formdata,
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
            "User-Name": props.userName,
            "User-Secret": props.userPassword ? props.userPassword : props.userSecret,
        }}
    )

    .then((response) => {
        callback && callback(response.data)
    })

    .catch((error) => {
        console.log('Send Messages Error', error)
    });

}

import axios from 'axios'
import * as str from '../../actions'

export function editMessage(props, chatId, messageId, data, callback) {
    axios.patch(
        `${str.getApiUrl(props)}/chats/${chatId}/messages/${messageId}/`,
        data,
        { headers: { 
            "Public-Key": props.publicKey ? props.publicKey : props.projectID,
